int randomi(int rmin, int rmax) {
	int d = rmax - rmin + 1;
	int r = (rand() % d) + rmin;
	// AddLog("random: " << rmin << "   " << r << "    " << rmax);
	return r;
}

int RandRange(int rmin, int rmax) {
	int d = rmax - rmin + 1;
	int r = (rand() % d) + rmin;
	// AddLog("random: " << rmin << "   " << r << "    " << rmax);
	return r;
}

float randomf(float rmin, float rmax) {
	float d = rmax - rmin;
	float r = (rand() % static_cast<int>(d)) + rmin;
	// AddLog("random: " << rmin << "   " << r << "    " << rmax);
	return r;
}

