//----------------------------------------------------------------
 
void TrueTypeWrapper::Render(Graphic::cRenderDevice &dev) {
	if (m_Chars.empty())
		return;

	m_VAO.Bind();
	uint32_t id = 0;
	dev.CurrentShader()->SetBackColor(m_Color);
	for (auto it : m_Chars) {
		if (!it->m_Ready)
			break;
		it->m_Texture.Bind();
		m_VAO.DrawElements(4, id * 4, 0, Graphic::Flags::fQuads);
		++id;
	}
	m_VAO.UnBind();
}

